import { useEffect, useState } from 'react';
import '../params/FormQuestion.css';

function FormQuestion(){

    const [options,setOptions] =  useState([]);

    const [ formValues, setFormValues]=useState({
        selectedProfil:'',
        question:'',
        reponse:'',
    });

    const [showOptionsForm,setShowOptionForm]=useState(false);
    const [errors, setErrors] = useState({});

    useEffect(() => {
        fetch('http://localhost:8080/rh_back/ProfilController')
            .then(response => Response.json())
            .then(data => setOptions(data))
            .catch(error => console.error('erreur lors de la recuperation des données : ',error));

    }, []);

    // pour les INPUT
    const handleInputQuestion = event => {
        console.log("selected : ", event.target.value);
        const value= event.target.value;
        setFormValues({...formValues,question:value});
        setErrors(prevErrors =>({
            ...prevErrors,
            question: value ? "" : "Ce champ est obligatoire"
        }));
    };

    const handleInputReponse = event => {
        console.log("selected : ", event.target.value);
        setFormValues(prevState =>({
            ...prevState,
            reponse: event.target.value,
        }));
    };
// --end

// BOUTON OPTION REPONSE
    const handleAnswerOption = () => {
       const gsonData = {
            critereid:formValues.selectedProfil,
            question:formValues.question,
            reponse:formValues.reponse,
       } ;
       console.log(JSON.stringify(gsonData));

       setShowOptionForm(true);
    }
//--end 

// BOUTON OPTION VALIDER
    const handleSubmit = () => {
        const gsonData = {
             reponseone:formValues.reponseone,
             reponsetwo:formValues.reponsetwo,
        } ;
        console.log(JSON.stringify(gsonData));
     }
//--end 

    return(
        <div id="FormQuestionContent">
            
            <div className='FormQuestion'>

                <label>Choisissez un profil : </label>
                <select value={formValues.selectedProfil}>
                    {options.map(option => (
                        <option key={option.id} value={parseInt(option.id,10)}></option>
                    ))}
                </select>

                <label>Question : </label>
                <input
                    type="text"
                    value={formValues.question}
                    onChange={handleInputQuestion}
                    placeholder="question"   
                />
                {errors.question && <span className="error">{errors.question}</span>}
                
                <label>Réponse : </label>
                <input
                    type="text"
                    value={formValues.reponse}
                    onChange={handleInputReponse}
                    placeholder="reponse"
                    required   
                />

                <button onClick={handleAnswerOption}>Valider</button>
            </div>

            {showOptionsForm && (
                <div className='showOptionsForm'>

                    <h2>Options de reponse</h2>
                    <h3>Question: {formValues.question} ?</h3>

                    <label>Reponse1 : </label>
                    <input
                        type='text'
                        value={formValues.reponseone}
                        placeholder='reponse1'
                    />
                    <label>Reponse2 : </label>
                    <input
                        type='text'
                        value={formValues.reponsetwo}
                        placeholder='reponse2'
                    />

                    <button onClick={handleSubmit}>Ajouter</button>
                
                </div>
            )};

        </div>
    );
    
}

export default FormQuestion;
