import React from "react";

class Input extends React.Component{
    render(){
        const {label,type,placeholder}=this.props;

        return(
            <p>
                <label>{label}</label>
                <input type={type} placeholder={placeholder}></input>
            </p>
        );
    }
}
export default Input;
