import './Container.css';
import React from 'react';
import Product from './Product';
import Input from './Input';
import Button from './Button';


class Container extends React.Component {
    render(){
        const { title } =this.props;

        return(
            <div className='container'>
                    <h2>{title}</h2> 
                    
                    <div className='item'>
                        <Product title="produit" imagesrc="./images/im.png" imagealt="image" buttontext="Add to card" />
                        <Product title="produit" imagesrc="./images/im.png" imagealt="image" buttontext="Add to card" />
                        <Product title="produit" imagesrc="./images/im.png" imagealt="image" buttontext="Add to card" />
                        <Product title="produit" imagesrc="./images/im.png" imagealt="image" buttontext="Add to card" />
                        <Product title="produit" imagesrc="./images/im.png" imagealt="image" buttontext="Add to card" />
                        <Product title="produit" imagesrc="./images/im.png" imagealt="image" buttontext="Add to card" />
                    </div>

                    <div className='filter'>
                        <h2>Filter</h2>
                        <Input label="Price min:" type="text" placeholder="text"/>
                        <Input label="Price max:" type="text" placeholder="text"/>
                        <Button name="search"/>
                    </div>
            </div>
        ); 

    }

}
export default Container;
