import Button from "./Button";
import Image from "./Image";

export function Product(){
    const{title,imagesrc,imagealt,buttontext,onbuttonclick}=this.props;

    return(
        <div className="card">
            <Image src={imagesrc} alt={imagealt} width="300" height="200"/>
            <h2>{title}</h2>
            <Button name={buttontext} onClick={onbuttonclick}/>
        </div>
    );
}
export default Product;
